package classRoomPrograms;

public class DoWhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
//        Trying out do-while loop

	      do{
	         System.out.print("value of x : " + x + "\n");
	         x++;
	      }
	      while( x < 20 );
	   
	}

}
